mapper.select:
SELECT COUNT(1)
FROM users
WHERE user_id = #{userId}

SELECT user_id AS userId, user_name AS username, user_pw AS userPw
FROM users
WHERE user_id = #{userId}

SELECT auth_code
FROM user_auth
WHERE user_id = #{userId}

mapper.update:
UPDATE users
SET user_name = #{username}
WHERE user_id = #{userId}

UPDATE users
SET user_pw = #{userPw}
WHERE user_id = #{userId}
